                D[w]=D[v]+G.arcs[v][w];     //更新D[w]
                Path [w]=v;                     //更改w的前驱为v
            }
    }
    cout<<"以v0为起点的图的最短路径为:"<<endl;
    for(int i=0;i<G.vexnum;i++)
    {
        cout<<"v0->v"<<i<<":";
        if(D[i]!=MaxInt) //可以到达
        {
            cout<<D[i]<<endl;
        }
        else
        {
            cout<<"不可达，无最短路径"<<endl;
        }
    }
}
void Menu()
{
